var terms = {};
terms['ch8-gt-experiencemortalityrates'] = '<p class="term">experience mortality rate</p> <p class="definition">The historical rate of death in a given group of people.  <a href="../chapter8/page3.xhtml#loma371-ch8-span-8" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch9-gt-conditionallyvestedcommission'] = '<p class="term">conditionally vested commission</p> <p class="definition">A commission that becomes vested only after an agent reaches a certain age or number of years with the company.  <a href="../chapter9/page4.xhtml#loma371-ch9-span-40" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch8-gt-mortalityimprovementscale'] = '<p class="term">mortality improvement scale</p> <p class="definition">A published set of recommended adjustments to mortality tables.  <a href="../chapter8/page6.xhtml#loma371-ch8-span-52" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch10-gt-guaranteedmaximummortalityrate'] = '<p class="term">guaranteed maximum mortality rate</p> <p class="definition">For universal life (UL) products, a monetary amount specified in the contract which sets an upper limit on the mortality charge.  <span class="return">See Module 4, Chapter 10.</span></p>';
terms['ch10-gt-indexeduniversallifeinsurance'] = '<p class="term">indexed universal life (IUL) insurance</p> <p class="definition">A type of universal life insurance that offers certain principal value and earnings guarantees, but also offers the possibility of additional earnings by linking the cash value to a published index. Also called equity indexed universal life insurance.  <span class="return">See Module 4, Chapter 10.</span></p>';
terms['ch2-gt-currencyrisk'] = '<p class="term">currency risk</p> <p class="definition">The risk arising from changes in currency exchange rates. Also known as foreign exchange risk. <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch9-gt-lapserate'] = '<p class="term">lapse rate</p> <p class="definition">The ratio of business in force that terminates for nonpayment of premium during a given period to the total business in force at the beginning of that period.  <a href="../chapter9/page5.xhtml#loma371-ch9-span-61" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch2-gt-baseliii'] = '<p class="term">Basel III</p> <p class="definition">A comprehensive set of financial supervision reform measures, developed by the Basel Committee on Banking Supervision (BCBS), and designed to strengthen the regulation, supervision, and risk management of the banking, insurance, and securities sectors globally.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch3-gt-risktaxonomy'] = '<p class="term">risk taxonomy</p> <p class="definition">The classification and aggregation of all risks across an organization based on each risk&#8217;s core, defining characteristics.  <span class="return">See Module 1, Chapter 3.</span></p>';
terms['ch7-gt-spreadexpansion'] = '<p class="term">spread expansion</p> <p class="definition">The widening of an insurer&#8217;s interest spread. <a href="../chapter7/page4.xhtml#loma371-ch7-span-58" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch7-gt-futurevalueinterestfactor'] = '<p class="term">future value interest factor (FVIF)</p> <p class="definition">The future value of $1.00 at a given interest rate for a stated number of periods. <a href="../chapter7/page3.xhtml#loma371-ch7-span-22" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch4-gt-pointestimate'] = '<p class="term">point estimate</p> <p class="definition">In financial modeling, an estimate that is assigned a single value.  <span class="return">See Module 1, Chapter 4.</span></p>';
terms['ch1-gt-experiencedata'] = '<p class="term">experience data</p> <p class="definition">Internal, proprietary data about the past behavior of certain variables such as mortality rates and expenses.  <span class="return">See Module 1, Chapter 1.</span></p>';
terms['ch2-gt-conferenceofsponsoringorganizationsofthetreadwaycommission'] = '<p class="term">Conference of Sponsoring Organizations of the Treadway Commission (COSO)</p> <p class="definition">A consortium of self-regulatory member organizations that provides frameworks and guidance on enterprise risk management, internal control, and fraud defense. <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch11-gt-immediateannuity'] = '<p class="term">immediate annuity</p> <p class="definition">An annuity contract under which periodic annuity payments begin no more than one year after the contract is purchased. <span class="return">See Module 4, Chapter 11.</span></p>';
terms['ch3-gt-cashflowmatching'] = '<p class="term">cash-flow matching</p> <p class="definition">A risk management strategy that involves identifying the patterns of cash outflows for insurance products and matching those cash outflows with a selection of assets that will produce a similar pattern of cash inflows. <span class="return">See Module 1, Chapter 3.</span></p>';
terms['ch1-gt-riskmanagement'] = '<p class="term">risk management</p> <p class="definition">The process of systematically identifying, assessing, and minimizing the negative impact of risk. <span class="return">See Module 1, Chapter 1.</span></p>';
terms['ch7-gt-accumulatedvalue'] = '<p class="term">accumulated value</p> <p class="definition">For a deferred annuity contract, the total amount of premiums and earnings, less the amount of any withdrawals or charges assessed by the insurer. Also known as account value.  <a href="../chapter7/page4.xhtml#loma371-ch7-span-53" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch4-gt-randomsample'] = '<p class="term">random sample</p> <p class="definition">A statistical sample in which each possible value is equally likely to be selected. <span class="return">See Module 1, Chapter 4.</span></p>';
terms['ch5-gt-marketingprojections'] = '<p class="term">marketing projections</p> <p class="definition">In a comprehensive business analysis, preliminary sales and financial forecasts that include estimates of potential unit sales, revenues, costs, and profits for a proposed product. <span class="return">See Module 2, Chapter 5.</span></p>';
terms['ch2-gt-reinvestmentraterisk'] = '<p class="term">reinvestment-rate risk</p> <p class="definition">The risk that a change in interest rates will lead to lower income when owners reinvest their funds. <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch4-gt-nonnormaldistribution'] = '<p class="term">non-normal distribution</p> <p class="definition">An asymmetrical probability distribution in which generally the number of values on one side of the mean is greater than the number of values on the other side of the mean.  <span class="return">See Module 1, Chapter 4.</span></p>';
terms['ch10-gt-waiverofpremiumfordisabilitybenefit'] = '<p class="term">waiver of premium for disability benefit</p> <p class="definition">A supplemental life insurance policy benefit under which the insurer promises to give up its right to collect premiums that become due while the insured is disabled according to the policy or rider&#8217;s definition of disability.  <span class="return">See Module 4, Chapter 10.</span></p>';
terms['ch4-gt-randomnumbergenerator'] = '<p class="term">random number generator</p> <p class="definition">A software application that automatically identifies a pattern of values that would be produced by sampling from a given probability distribution.  <span class="return">See Module 1, Chapter 4.</span></p>';
terms['ch6-gt-favorabledeviation'] = '<p class="term">favorable deviation</p> <p class="definition">In product operations, a difference between actual and assumed product values that produces an increase in actual product profitability relative to assumed product profitability.  <span class="return">See Module 2, Chapter 6.</span></p>';
terms['ch9-gt-costobject'] = '<p class="term">cost object</p> <p class="definition">Any purpose for which a business measures costs. <a href="../chapter9/page4.xhtml#loma371-ch9-span-32" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch11-gt-pointtopointmethod'] = '<p class="term">point-to-point method</p> <p class="definition">For fixed indexed annuities, an index-crediting mechanism that compares the value of the index at the start of the annuity contract term to its value at the end of the term to determine what, if any, excess interest has accrued because of a change in the index. <span class="return">See Module 4, Chapter 11.</span></p>';
terms['ch7-gt-guaranteedinterestcreditingrate'] = '<p class="term">guaranteed interest-crediting rate</p> <p class="definition">The minimum interest rate an insurer must pay on a contract&#8217;s accumulated value.  <a href="../chapter7/page4.xhtml#loma371-ch7-span-48" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch2-gt-actuarialmemorandum'] = '<p class="term">actuarial memorandum</p> <p class="definition">The document in an Actuarial Opinion and Memorandum (AOM) consisting of a formal report containing documentation supporting the conclusion expressed in the actuarial opinion.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch2-gt-pricingrisk'] = '<p class="term">pricing risk (C-2 risk)</p> <p class="definition">The contingency risk that an insurer&#8217;s actual experience with pricing factors, such as mortality rates, operating expenses, or policyholder behavior, will differ significantly from the actuarial assumptions used in the product&#8217;s financial design, causing the insurer to lose money on its products.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch1-gt-valuation'] = '<p class="term">valuation</p> <p class="definition">The process of calculating the monetary value of a company&#8217;s assets, liabilities, and capital for accounting and financial reporting purposes.  <span class="return">See Module 1, Chapter 1.</span></p>';
terms['ch9-gt-withdrawal'] = '<p class="term">withdrawal</p> <p class="definition">A transaction in which (1) the owner of a life insurance policy elects to receive a portion of the policy&#8217;s cash value while the policy is in force, or (2) the owner of a deferred annuity contract elects to receive a portion of the contract&#8217;s accumulated value during the accumulation period. Also known as partial surrender or policy withdrawal.  <a href="../chapter9/page5.xhtml#loma371-ch9-span-60" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch3-gt-ethicalbehavior'] = '<p class="term">ethical behavior</p> <p class="definition">Behavior that meets accepted standards of moral conduct. <span class="return">See Module 1, Chapter 3.</span></p>';
terms['ch4-gt-normaldistribution'] = '<p class="term">normal distribution</p> <p class="definition">A probability distribution in which the number of values that are less than the mean is the same as the number of values greater than the mean, and in which the values are dispersed in a specific proportion. <span class="return">See Module 1, Chapter 4.</span></p>';
terms['ch8-gt-publishedmortalitytables'] = '<p class="term">published mortality table</p> <p class="definition">A type of mortality table that is based on the mortality of the public at large or a broad cross-section of the population and that is made available to the general public through printed documents and electronic sources.  <a href="../chapter8/page5.xhtml#loma371-ch8-span-41" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch11-gt-accumulationperiod'] = '<p class="term">accumulation period</p> <p class="definition">For a deferred annuity, the period that begins on the contract&#8217;s purchase date and runs until either the beginning of the payout period or the date when the owner terminates the contract. <span class="return">See Module 4, Chapter 11.</span></p>';
terms['ch2-gt-financialassistanceandsolvencytrackingsystem'] = '<p class="term">Financial Assistance and Solvency Tracking (FAST) System</p> <p class="definition">In the United States, a collection of analytical solvency tools used to evaluate the solvency of insurers that includes a ratio-based evaluation system established by the National Association of Insurance Commissioners (NAIC); based on (1) ratio analysis of an insurer&#8217;s most recent financial statements and (2) analysis of a five-year history of specific aspects of the insurer&#8217;s financial statements.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch10-gt-waiverofpremiumforpayorbenefit'] = '<p class="term">waiver of premium for payor benefit</p> <p class="definition">A supplemental life insurance policy benefit which provides that the insurer will waive its right to collect a policy&#8217;s renewal premiums if the payor&#8212;the person paying the policy premiums&#8212;dies or becomes totally disabled. <span class="return">See Module 4, Chapter 10.</span></p>';
terms['ch3-gt-liabilityportfolio'] = '<p class="term">liability portfolio</p> <p class="definition">In asset-liability management (ALM), the portfolio in which the insurer represents the insurer&#8217;s obligations to customers.  <span class="return">See Module 1, Chapter 3.</span></p>';
terms['ch2-gt-actuarialopinion'] = '<p class="term">actuarial opinion</p> <p class="definition">The document in an Actuarial Opinion and Memorandum (AOM) which states that the company&#8217;s reserves are adequate for the anticipated cash flows arising from the insurer&#8217;s contractual obligations, given the assets supporting them.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch4-gt-probabilitydistribution'] = '<p class="term">probability distribution</p> <p class="definition">A data distribution that includes both data values and the probability of observing each value. <span class="return">See Module 1, Chapter 4.</span></p>';
terms['ch8-gt-tabularmortalityrates'] = '<p class="term">tabular mortality rate</p> <p class="definition">A mortality rate shown in a mortality table.  <a href="../chapter8/page2.xhtml#loma371-ch8-span-6" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch2-gt-contingencyrisks'] = '<p class="term">contingency risks (C-risks)</p> <p class="definition">In the United States, four officially recognized categories of risk that regulators have identified as being crucial to an insurer&#8217;s cash flows and that directly affect an insurer&#8217;s capital adequacy.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch6-gt-contingencypricingstructure'] = '<p class="term">contingency pricing structure</p> <p class="definition">A variation in premium rate structures for individual life insurance products that gives an insurer mechanisms for adjusting the company&#8217;s charges for an in-force product to reflect the company&#8217;s financial results for the product. <span class="return">See Module 2, Chapter 6.</span></p>';
terms['ch9-gt-surrendercharge'] = '<p class="term">surrender charge</p> <p class="definition">A penalty charge imposed when the owner of a cash value life insurance policy surrenders the policy for its cash surrender value, or if the owner of a deferred annuity contract surrenders the contract within a stated number of years after it was purchased. <a href="../chapter9/page6.xhtml#loma371-ch9-span-86" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch1-gt-policydividends'] = '<p class="term">policy dividends</p> <p class="definition">Returns of excess premium paid to the owners of individual participating life insurance policies.  <span class="return">See Module 1, Chapter 1.</span></p>';
terms['ch8-gt-sexdistinctmortalitytables'] = '<p class="term">sex-distinct mortality table</p> <p class="definition">A type of mortality table that shows different mortality rates for males and females. <a href="../chapter8/page5.xhtml#loma371-ch8-span-38" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch6-gt-day2functionality'] = '<p class="term">Day 2 functionality</p> <p class="definition">Product administration and IT elements that are necessary at some future date to service and administer an insurance product, but which can be implemented after the product has been launched. <span class="return">See Module 2, Chapter 6.</span></p>';
terms['ch8-gt-ultimatemortalitytable'] = '<p class="term">ultimate mortality table</p> <p class="definition">A type of mortality table that shows the expected mortality rates of people who have not recently been underwritten for insurance policies. <a href="../chapter8/page5.xhtml#loma371-ch8-span-35" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch10-gt-cashsurrendervalue'] = '<p class="term">cash surrender value</p> <p class="definition">The amount of the cash value that a policyowner is entitled to receive from the insurer upon surrender of the policy. Also known as the surrender value or surrender benefit.  <span class="return">See Module 4, Chapter 10.</span></p>';
terms['ch8-gt-basicmortalitytables'] = '<p class="term">basic mortality table</p> <p class="definition">A type of mortality table that has no margin built into the rates, is used for technical product design, and provides realistic mortality rates so that an insurer can best estimate future mortality costs.  <a href="../chapter8/page5.xhtml#loma371-ch8-span-31" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch2-gt-geopoliticalrisk'] = '<p class="term">geopolitical risk</p> <p class="definition">The risk of loss due to unexpected changes in international relationships, political alignments, socioeconomics, or cultural shifts.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch4-gt-assetadequacyanalysis'] = '<p class="term">asset adequacy analysis</p> <p class="definition">A broad actuarial practice undertaken to ensure that the assets backing reserves meet established standards.  <span class="return">See Module 1, Chapter 4.</span></p>';
terms['ch2-gt-continuationcoverage'] = '<p class="term">continuation coverage</p> <p class="definition">The coverage provided to the customers of a failed insurance company under a specific kind of reinsurance arrangement.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch7-gt-currentinterestcreditingrate'] = '<p class="term">current interest-crediting rate</p> <p class="definition">The interest rate an insurer declares and pays if a fixed-rate contract remains in force for a specified period of time. <a href="../chapter7/page4.xhtml#loma371-ch7-span-49" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch11-gt-fixedperiodoption'] = '<p class="term">fixed period option</p> <p class="definition">A guaranteed annuity payout option under which the insurer makes annuity payments for a specified period of time regardless of whether the annuitant is living.  <span class="return">See Module 4, Chapter 11.</span></p>';
terms['ch9-gt-costaccountingsysteym'] = '<p class="term">cost accounting systeym</p> <p class="definition">An accounting subsystem that accumulates expense data for the dual purposes of effective cost control and accurate product design activities.  <a href="" class="return">Return to text.</a></p>';
terms['ch5-gt-feasibilitystudy'] = '<p class="term">feasibility study</p> <p class="definition">An evaluation of whether a company has the capability to take a new product to market.  <span class="return">See Module 2, Chapter 5.</span></p>';
terms['ch2-gt-lapse'] = '<p class="term">lapse</p> <p class="definition">The termination of a life insurance policy as a result of nonpayment of the policy premium. <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch7-gt-presentvalue'] = '<p class="term">present value (PV)</p> <p class="definition">The amount of a sum that, if invested at a specified interest rate on a specified date, would grow to equal a specified future amount.  <a href="../chapter7/page3.xhtml#loma371-ch7-span-14" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch1-gt-investment'] = '<p class="term">investment</p> <p class="definition">Any use of a company&#8217;s resources that is intended to generate a profit or positive return of some type. <span class="return">See Module 1, Chapter 1.</span></p>';
terms['ch11-gt-beneficiary'] = '<p class="term">beneficiary</p> <p class="definition">The person or legal entity who may receive benefits accrued or values remaining in an annuity contract upon the death of the contract owner or annuitant.  <span class="return">See Module 4, Chapter 11.</span></p>';
terms['ch4-gt-futuresstudies'] = '<p class="term">futures studies</p> <p class="definition">Studies that identify possible developments that could disrupt trends&#8212;thereby changing the extrapolation of the values from the past&#8212;and then produce an array of possibilities for future values based on these developments. <span class="return">See Module 1, Chapter 4.</span></p>';
terms['ch11-gt-index'] = '<p class="term">index</p> <p class="definition">A statistical measurement system that tracks the performance of a group of similar investments.  <span class="return">See Module 4, Chapter 11.</span></p>';
terms['ch3-gt-assetportfolio'] = '<p class="term">asset portfolio</p> <p class="definition">In asset-liability management (ALM), the portfolio in which the insurer holds securities and other invested assets.  <span class="return">See Module 1, Chapter 3.</span></p>';
terms['ch9-gt-assetbasedcommissionschedule'] = '<p class="term">asset-based commission schedule</p> <p class="definition">For annuity sales, a commission schedule that calculates commissions as a percentage of the accumulated value of a deferred annuity contract&#8217;s funds. In addition, commissions are payable as long as the contract remains in force, even if the contract owner stops making premium payments.  <a href="../chapter9/page4.xhtml#loma371-ch9-span-43" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch1-gt-actuarialassumption'] = '<p class="term">actuarial assumption</p> <p class="definition">An estimated value used in financial modeling, typically for the purposes of risk management or technical product design.  <span class="return">See Module 1, Chapter 1.</span></p>';
terms['ch8-gt-mortalityrate'] = '<p class="term">mortality rate</p> <p class="definition">The rate at which death occurs among a defined group of people during a specified period, usually one year.  <a href="../chapter8/page2.xhtml#loma371-ch8-span-4" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch8-gt-populationmortalitytable'] = '<p class="term">population mortality table</p> <p class="definition">A type of mortality table that shows mortality statistics for all members of a given population. <a href="../chapter8/page5.xhtml#loma371-ch8-span-27" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch8-gt-mortalitymargin'] = '<p class="term">mortality margin</p> <p class="definition">A provision for conservatism in mortality risk projections.  <a href="../chapter8/page7.xhtml#loma371-ch8-span-64" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch5-gt-ratechangeproject'] = '<p class="term">rate change project</p> <p class="definition">A project that involves changing rates for adjustable policy elements, such as administrative fees and mortality and expense charges. Also known as repricing project.  <span class="return">See Module 2, Chapter 5.</span></p>';
terms['ch3-gt-governance'] = '<p class="term">governance</p> <p class="definition">The combination of people, rules, controls, and policies put in place to provide oversight and direction for the organization&#8217;s operations and actions so that the organization can achieve its strategic objectives.  <span class="return">See Module 1, Chapter 3.</span></p>';
terms['ch3-gt-nondiversifiablerisk'] = '<p class="term">nondiversifiable risk</p> <p class="definition">Risk that affects all assets in an economic system and is therefore not specific to an individual asset or issuer. Also known as systematic risk. <span class="return">See Module 1, Chapter 3.</span></p>';
terms['ch8-gt-annuitymortalitytable'] = '<p class="term">annuity mortality table</p> <p class="definition">A type of mortality table that shows the projected mortality rates and survival rates for a population of annuitants only.  <a href="../chapter8/page5.xhtml#loma371-ch8-span-30" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch2-gt-defaultrisk'] = '<p class="term">default risk</p> <p class="definition">In the context of investing, the type of credit risk that involves the risk that an insurer will not receive the cash flows to which it is entitled because a party with which the insurer has a financial arrangement is late with payments or entirely fails to fulfill its debt obligations.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch2-gt-surrender'] = '<p class="term">surrender</p> <p class="definition">A transaction in which the owner of a cash value life insurance policy or a deferred annuity chooses to receive the contract&#8217;s monetary value before the contract matures. <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch3-gt-internalaudit'] = '<p class="term">internal audit</p> <p class="definition">An examination of the company&#8217;s records, policies, and procedures conducted by a person who works for the organization, typically in the compliance area. <span class="return">See Module 1, Chapter 3.</span></p>';
terms['ch2-gt-interestraterisk'] = '<p class="term">interest-rate risk (C-3 risk)</p> <p class="definition">The contingency risk that movements in market interest rates will cause an insurer&#8217;s assets to lose value or its liabilities to gain value.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch1-gt-mortalitytable'] = '<p class="term">mortality table</p> <p class="definition">A statistical table that shows the number of people in a cohort&#8212;or group of people&#8212;and the number or rate of deaths for the cohort at given ages&#8212;that is, how many deaths may be expected at each age. <span class="return">See Module 1, Chapter 1.</span></p>';
terms['ch10-gt-levelpremium'] = '<p class="term">level premium</p> <p class="definition">For life insurance products, a form of premium payment in which periodic premium payments are equal in amount and are generally paid monthly, quarterly, or annually; typically, level premiums purchase multiyear term life insurance and whole life insurance policies. <span class="return">See Module 4, Chapter 10.</span></p>';
terms['ch2-gt-expensebudget'] = '<p class="term">expense budget</p> <p class="definition">A financial plan of action that identifies the types and amounts of expenses a company expects to incur during an accounting period.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch5-gt-legalandtaxreview'] = '<p class="term">legal and tax review</p> <p class="definition">An evaluation of any legal or tax concerns associated with a new product.  <span class="return">See Module 2, Chapter 5.</span></p>';
terms['ch8-gt-projectionmethod'] = '<p class="term">projection method</p> <p class="definition">A method of modifying tabular mortality that involves multiplying the tabular mortality rates by a chosen percentage.  <a href="../chapter8/page6.xhtml#loma371-ch8-span-53" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch11-gt-deferredannuity'] = '<p class="term">deferred annuity</p> <p class="definition">An annuity under which periodic income payments are scheduled to begin more than one annuity period after the date on which the annuity was purchased.  <span class="return">See Module 4, Chapter 11.</span></p>';
terms['ch9-gt-fullcosting'] = '<p class="term">full costing</p> <p class="definition">An accounting method for estimating product-related expenses in which direct and indirect expenses are both counted. Also known as absorption costing. <a href="../chapter9/page4.xhtml#loma371-ch9-span-29" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch2-gt-underwritingphilosophy'] = '<p class="term">underwriting philosophy</p> <p class="definition">A set of objectives that guides all of an insurer&#8217;s underwriting actions when selecting risk. <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch2-gt-insuranceregulatoryinformationsystem'] = '<p class="term">Insurance Regulatory Information System (IRIS)</p> <p class="definition">In the United States, a tool within the FAST system that monitors the financial condition of insurers with a view toward preventing insolvencies which uses 12 standardized financial ratios to identify insurance companies that are likely to experience financial difficulties. <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch6-gt-negativetailscenario'] = '<p class="term">negative tail scenario</p> <p class="definition">A highly unfavorable but highly unlikely scenario identified in a stochastically modeled product design as a potential outcome.  <span class="return">See Module 2, Chapter 6.</span></p>';
terms['ch5-gt-productdesignobjectives'] = '<p class="term">product design objectives</p> <p class="definition">For life insurance or annuity products, specifications that document the product&#8217;s basic characteristics, such as its benefits, any limits on the contract amount or the applicant&#8217;s age at issue, any special features, the commission range, and any settlement options. <span class="return">See Module 2, Chapter 5.</span></p>';
terms['ch7-gt-ordinaryannuity'] = '<p class="term">ordinary annuity</p> <p class="definition">A series of equal periodic payments made at the end of each payment period over a fixed amount of time. <a href="../chapter7/page3.xhtml#loma371-ch7-span-24" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch7-gt-priceappreciation'] = '<p class="term">price appreciation</p> <p class="definition">An increase in the market value of an invested asset.  <a href="../chapter7/page3.xhtml#loma371-ch7-span-8" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch2-gt-eventrisk'] = '<p class="term">event risk</p> <p class="definition">The risk that a single, unforeseen occurrence will negatively affect a company&#8217;s financial position. <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch10-gt-accelerateddeathbenefit'] = '<p class="term">accelerated death benefit</p> <p class="definition">A supplemental life insurance policy benefit that typically allows the policyowner to elect to receive all or part of the policy&#8217;s death benefit before the insured&#8217;s death if certain conditions are met. Also known as a living benefit.  <span class="return">See Module 4, Chapter 10.</span></p>';
terms['ch2-gt-operationalrisk'] = '<p class="term">operational risk</p> <p class="definition">The risk of financial loss resulting from inadequate or failed internal processes, controls, systems, and people.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch7-gt-presentvalueinterestfactor'] = '<p class="term">present value interest factor (PVIF)</p> <p class="definition">The present value of $1.00 discounted at a given interest rate for a stated number of periods.  <a href="../chapter7/page3.xhtml#loma371-ch7-span-30" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch3-gt-threelinesofdefense'] = '<p class="term">three lines of defense</p> <p class="definition">A comprehensive, systematic approach to enhancing communication on risk management and controls by assigning clear responsibilities and roles to each of the following lines: (1) operational management, (2) risk management and compliance, and (3) internal audit.  <span class="return">See Module 1, Chapter 3.</span></p>';
terms['ch2-gt-riskbasedcapitalforinsurersmodelact'] = '<p class="term">Risk-Based Capital for Insurers Model Act</p> <p class="definition">A National Association of Insurance Commissioners (NAIC) model law that establishes the risk-based capital (RBC) requirements and enables state insurance departments to identify potentially impaired insurers.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch1-gt-bigdata'] = '<p class="term">big data</p> <p class="definition">A large amount of information gathered from various sources, in various formats, and at a rapid speed.  <span class="return">See Module 1, Chapter 1.</span></p>';
terms['ch2-gt-riskbasedcapitalrequirements'] = '<p class="term">risk-based capital (RBC) requirements</p> <p class="definition">A set of capital requirements developed by the National Association of Insurance Commissioners (NAIC) to determine the minimum capital level of an insurer, based on the insurer&#8217;s size and risk profile, as identified by a specific risk-weighted formula based on four classifications of risks, called contingency risks.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch6-gt-derivativesecurity'] = '<p class="term">derivative security</p> <p class="definition">A financial security, such as a stock option, that derives its value from another security. Also known as a derivative.  <span class="return">See Module 2, Chapter 6.</span></p>';
terms['ch1-gt-enterpriseriskmanagement'] = '<p class="term">enterprise risk management (ERM)</p> <p class="definition">A system that identifies and quantifies risks from both potential threats and potential opportunities and manages these risks in a coordinated approach that supports the organization&#8217;s strategic objectives. <span class="return">See Module 1, Chapter 1.</span></p>';
terms['ch1-gt-assetliabilitymanagement'] = '<p class="term">asset-liability management (ALM)</p> <p class="definition">In an actuarial context, the practice of coordinating the administration of an insurer&#8217;s asset portfolio (its investments) with the administration of its liability portfolio (its obligations to customers) so as to achieve the best possible effects.  <span class="return">See Module 1, Chapter 1.</span></p>';
terms['ch7-gt-rateofreturn'] = '<p class="term">rate of return</p> <p class="definition">The earnings on an investment expressed as a percentage of the principal.  <a href="../chapter7/page3.xhtml#loma371-ch7-span-10" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch3-gt-creditingrateresolution'] = '<p class="term">crediting-rate resolution</p> <p class="definition">A formal declaration made by the board of directors of the rate of interest the insurer will credit on customers&#8217; money held in interest-bearing products.  <span class="return">See Module 1, Chapter 3.</span></p>';
terms['ch9-gt-surrenderrate'] = '<p class="term">surrender rate</p> <p class="definition">The ratio of the number of contracts surrendered during a given period to the total number of contracts in force at the beginning of that period.  <a href="../chapter9/page5.xhtml#loma371-ch9-span-62" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch1-gt-contractualreserves'] = '<p class="term">contractual reserves</p> <p class="definition">The amount that, together with future premiums and investment income, an insurer estimates it will need to pay policy benefits as they come due.  <span class="return">See Module 1, Chapter 1.</span></p>';
terms['ch11-gt-floorprovision'] = '<p class="term">floor provision</p> <p class="definition">For fixed indexed annuities, a provision which specifies that the contract&#8217;s values are not reduced if the linked index decreases in value during a measurement period. <span class="return">See Module 4, Chapter 11.</span></p>';
terms['ch11-gt-referenceindex'] = '<p class="term">reference index</p> <p class="definition">The index specified in a fixed indexed annuity contract; variations in the index determine whether the contract&#8217;s accumulated value is awarded interest credits. Also known as the benchmark index. <span class="return">See Module 4, Chapter 11.</span></p>';
terms['ch5-gt-salesvolume'] = '<p class="term">sales volume</p> <p class="definition">The number of units of product sold.  <span class="return">See Module 2, Chapter 5.</span></p>';
terms['ch4-gt-dynamicassumptions'] = '<p class="term">dynamic assumptions</p> <p class="definition">In financial modeling, variables that change over time, but in a way that is predictable and formula driven.  <span class="return">See Module 1, Chapter 4.</span></p>';
terms['ch8-gt-ultimatemortalityperiod'] = '<p class="term">ultimate mortality period</p> <p class="definition">The period of time after the select mortality period has ended.  <a href="../chapter8/page5.xhtml#loma371-ch8-span-36" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch11-gt-payee'] = '<p class="term">payee</p> <p class="definition">The person or entity designated to receive the periodic income payments under an annuity contract. <span class="return">See Module 4, Chapter 11.</span></p>';
terms['ch11-gt-guaranteedminimumdeathbenefit'] = '<p class="term">guaranteed minimum death benefit (GMDB)</p> <p class="definition">A variable annuity contract feature which generally guarantees that the contract&#8217;s death benefit will at least equal a specified minimum amount. <span class="return">See Module 4, Chapter 11.</span></p>';
terms['ch5-gt-marketingcommittee'] = '<p class="term">marketing committee</p> <p class="definition">A senior management group whose primary role is to provide overall guidance and control of the product development process.  <span class="return">See Module 2, Chapter 5.</span></p>';
terms['ch8-gt-survivalrate'] = '<p class="term">survival rate</p> <p class="definition">The percentage of people within a group who have attained a given age and are expected to be alive at their next birthday.  <a href="../chapter8/page4.xhtml#loma371-ch8-span-25" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch10-gt-currentmortalityrate'] = '<p class="term">current mortality rate</p> <p class="definition">In universal life (UL) products, the monthly mortality rate actually used to calculate the monthly mortality charge; is generally substantially lower than the guaranteed maximum mortality rate. <span class="return">See Module 4, Chapter 10.</span></p>';
terms['ch7-gt-annuitydue'] = '<p class="term">annuity due</p> <p class="definition">A series of equal periodic payments for which the payment occurs at the beginning of each payment period.  <a href="../chapter7/page3.xhtml#loma371-ch7-span-25" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch8-gt-setbackmethod'] = '<p class="term">setback method</p> <p class="definition">A method of modifying tabular mortality rates that consists of showing future decreases in mortality for people of a given age by using the tabular mortality rate for a younger age.  <a href="../chapter8/page6.xhtml#loma371-ch8-span-57" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch7-gt-interestcreditingrate'] = '<p class="term">interest-crediting rate</p> <p class="definition">The interest rate credited to products on behalf of customers. Also known as crediting rate. <a href="../chapter7/page6.xhtml#ch7-span-ch7-gt-interestcreditingrate" class="return">Return to text.</a></p>';
terms['ch9-gt-fundoperatingexpensecharge'] = '<p class="term">fund operating expense charge</p> <p class="definition">A periodic charge that a fund manager assesses to cover its costs of managing variable subaccount assets, buying and selling securities, and administering trades.  <a href="../chapter9/page6.xhtml#loma371-ch9-span-89" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch1-gt-insolvency'] = '<p class="term">insolvency</p> <p class="definition">An insurer&#8217;s inability to meet its financial obligations on time. <span class="return">See Module 1, Chapter 1.</span></p>';
terms['ch1-gt-actuary'] = '<p class="term">actuary</p> <p class="definition">A technical expert in insurance, annuities, and financial instruments who applies mathematical knowledge to industry and company statistics to calculate various financial values.  <span class="return">See Module 1, Chapter 1.</span></p>';
terms['ch1-gt-riskappetite'] = '<p class="term">risk appetite</p> <p class="definition">The amount and type of risk that an organization is prepared to seek in the pursuit of profits or other strategic objectives.  <span class="return">See Module 1, Chapter 1.</span></p>';
terms['ch4-gt-dependentvariable'] = '<p class="term">dependent variable</p> <p class="definition">A variable that reacts to changes in other variables. By convention, dependent variables are labeled y. <span class="return">See Module 1, Chapter 4.</span></p>';
terms['ch3-gt-diversifiablerisk'] = '<p class="term">diversifiable risk</p> <p class="definition">Risk that is specific to an individual asset or issuer. Also known as nonsystematic risk and specific risk. <span class="return">See Module 1, Chapter 3.</span></p>';
terms['ch1-gt-reinsurance'] = '<p class="term">reinsurance</p> <p class="definition">A type of insurance that one insurance company purchases from another insurance company. <span class="return">See Module 1, Chapter 1.</span></p>';
terms['ch2-gt-inflationrisk'] = '<p class="term">inflation risk</p> <p class="definition">The risk that the price of goods and services will increase over time, causing a decline in the value of investments and returns.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch7-gt-netcashflow'] = '<p class="term">net cash flow</p> <p class="definition">For an insurance or annuity product, a product&#8217;s total cash inflows generated from premiums, investments, and other sources minus the total cash outflows generated by such items as commissions, expenses, and benefit payments; focuses only on cash. <a href="../chapter7/page5.xhtml#loma371-ch7-span-80" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch7-gt-presentvalueinterestfactorstable'] = '<p class="term">present value interest factors (PVIF) table</p> <p class="definition">A table that lists present value interest factors.  <a href="../chapter7/page3.xhtml#loma371-ch7-span-31" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch2-gt-economiccapital'] = '<p class="term">economic capital</p> <p class="definition">An estimate of the amount of capital that a financial institution calculates to internally manage its own risks. Also known as internal capital.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch7-gt-compoundingperiod'] = '<p class="term">compounding period</p> <p class="definition">Each of the interest periods used in future value calculations.  <a href="../chapter7/page3.xhtml#loma371-ch7-span-13" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch9-gt-heapedcommissionschedule'] = '<p class="term">heaped commission schedule</p> <p class="definition">For life insurance sales, a commission schedule that features relatively high first-year commissions and lower renewal commissions.  <a href="../chapter9/page4.xhtml#loma371-ch9-span-41" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch9-gt-nonvestedcommission'] = '<p class="term">nonvested commission</p> <p class="definition">A commission that is payable only if the agent still represents the company when the commission becomes due.  <a href="../chapter9/page4.xhtml#loma371-ch9-span-39" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch1-gt-return'] = '<p class="term">return</p> <p class="definition">Any reward, profit, or compensation an investor hopes to earn for taking a risk.  <span class="return">See Module 1, Chapter 1.</span></p>';
terms['ch4-gt-dynamicsolvencytesting'] = '<p class="term">dynamic solvency testing (DST)</p> <p class="definition">An application of dynamic financial analysis (DFA) that involves projecting an insurer&#8217;s capital as of a given valuation date and comparing the projected amounts of capital at various times after the valuation date.  <span class="return">See Module 1, Chapter 4.</span></p>';
terms['ch2-gt-reputationrisk'] = '<p class="term">reputation risk</p> <p class="definition">The risk of loss resulting from damage to or a decline in an insurer&#8217;s image, which can lead to the loss of customers&#8217; trust.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch6-gt-day1functionality'] = '<p class="term">Day 1 functionality</p> <p class="definition">Product administration and IT elements that a company must have in place and functioning when a product is first made available for sale.  <span class="return">See Module 2, Chapter 6.</span></p>';
terms['ch9-gt-fixedexpense'] = '<p class="term">fixed expense</p> <p class="definition">An expense that remains relatively constant regardless of the number of policies or contracts sold or the level of operating activity. <a href="../chapter9/page3.xhtml#loma371-ch9-span-13" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch1-gt-reservevaluation'] = '<p class="term">reserve valuation</p> <p class="definition">A formal actuarial process of establishing a value for an insurer&#8217;s reserves. Also known as reserving.  <span class="return">See Module 1, Chapter 1.</span></p>';
terms['ch5-gt-newproductproject'] = '<p class="term">new product project</p> <p class="definition">The most complex type of product development project, requiring the company to develop new product features and also requiring comprehensive regulatory filings.  <span class="return">See Module 2, Chapter 5.</span></p>';
terms['ch2-gt-financialconditionexamination'] = '<p class="term">financial condition examination</p> <p class="definition">In the United States, the part of an onsite formal regulatory audit that investigates two broad aspects of an insurer&#8217;s operations: (1) whether the insurer&#8217;s accounting records are accurate and the insurer is being operated on a sound and lawful basis, and (2) whether the insurer&#8217;s financial and business profiles contain any apparent hazards to the insurer&#8217;s solvency.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch11-gt-highwatermarkmethod'] = '<p class="term">high water mark method</p> <p class="definition">For fixed indexed annuities, an index-crediting mechanism that compares the value of the reference index at the beginning of the contract term with the highest value&#8212;or high water mark&#8212;that the index reaches on any contract anniversary date during the term. <span class="return">See Module 4, Chapter 11.</span></p>';
terms['ch2-gt-antiselectionrisk'] = '<p class="term">antiselection risk</p> <p class="definition">The risk of loss or adverse product performance due to the tendency of individuals who believe they have a greater-than-average likelihood of loss to seek insurance protection to a greater extent than do those who believe they have an average or less-than-average likelihood of loss. Also known as adverse selection risk.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch2-gt-rehabilitation'] = '<p class="term">rehabilitation</p> <p class="definition">In an insurance receivership, a legal condition in which regulators take over operation of the insolvent company from management and attempt to restore the company to solvent.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch7-gt-netpresentvalue'] = '<p class="term">net present value</p> <p class="definition">For an investment project, a monetary amount that is generally calculated by subtracting the initial investment in the project from the present value of the project&#8217;s earnings over a future period, usually the entire expected life of the project.  <a href="../chapter7/page5.xhtml#loma371-ch7-span-82" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch2-gt-annualstatement'] = '<p class="term">Annual Statement</p> <p class="definition">A financial report that every insurer in the United States must file annually, with interim quarterly reports, with the National Association of Insurance Commissioners (NAIC) and the insurance regulatory organization in each state in which the insurer conducts business to assist regulators with monitoring insurer solvency.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch2-gt-foreigninvestmentrisk'] = '<p class="term">foreign investment risk</p> <p class="definition">The risk of loss when investing in foreign companies if the government takes actions such as nationalization or taxation.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch7-gt-profitmargin'] = '<p class="term">profit margin</p> <p class="definition">For a life insurance or an annuity product, the present value of profits divided by the present value of premiums. <a href="../chapter7/page5.xhtml#loma371-ch7-span-86" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch2-gt-guarantyassociation'] = '<p class="term">guaranty association</p> <p class="definition">An organization that operates under the supervision of the state insurance commissioner to protect policyowners, insureds, beneficiaries, annuitants, payees, and assignees against losses that might result from the impairment or insolvency of an insurer that operates in that state.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch11-gt-fixedannuity'] = '<p class="term">fixed annuity</p> <p class="definition">An annuity contract under which the insurer guarantees (1) the minimum interest rate that it will apply to any accumulated value and (2) the minimum amount of the annuity payments that it will pay. <span class="return">See Module 4, Chapter 11.</span></p>';
terms['ch4-gt-confidencelimit'] = '<p class="term">confidence limit</p> <p class="definition">An expression of the probability that a specific outcome will fall within a stated range. <span class="return">See Module 1, Chapter 4.</span></p>';
terms['ch8-gt-generationalmortalitytable'] = '<p class="term">generational mortality table</p> <p class="definition">A type of mortality table in which mortality rates are dependent on both the person&#8217;s age and the year the person attains that age. Also known as a dynamic mortality table. <a href="../chapter8/page6.xhtml#loma371-ch8-span-51" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch11-gt-guaranteedminimumaccumulationbenefit'] = '<p class="term">guaranteed minimum accumulation benefit (GMAB)</p> <p class="definition">A variable annuity living benefit rider that guarantees a minimum protected value for a variable annuity contract even if its accumulated value declines because of poor investment performance. Usually, the contract owner must wait for a specified period before the guaranteed value becomes available.  <span class="return">See Module 4, Chapter 11.</span></p>';
terms['ch4-gt-conditionaltailexpectation'] = '<p class="term">conditional tail expectation (CTE)</p> <p class="definition">A statistical measure of the severity of tail risk, calculated as the average of all the values within a specified range of a probability distribution.  <span class="return">See Module 1, Chapter 4.</span></p>';
terms['ch7-gt-prospectivevaluationmethod'] = '<p class="term">prospective valuation method</p> <p class="definition">A method of determining a life insurance product&#8217;s cash value that looks at the product&#8217;s future cash flows.  <a href="../chapter7/page4.xhtml#loma371-ch7-span-52" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch10-gt-unitofcoverage'] = '<p class="term">unit of coverage</p> <p class="definition">A basic amount of coverage that insurers use when calculating premium rates for their products. For life insurance, a unit of coverage usually is $1,000 of coverage.  <span class="return">See Module 4, Chapter 10.</span></p>';
terms['ch11-gt-guaranteedminimumincomebenefit'] = '<p class="term">guaranteed minimum income benefit (GMIB)</p> <p class="definition">An annuity living benefit rider that guarantees a minimum protected value that can be converted into periodic income payments&#8212;usually after a waiting period of seven to ten years&#8212;even if the annuity&#8217;s accumulated value declines because of poor investment performance. <span class="return">See Module 4, Chapter 11.</span></p>';
terms['ch6-gt-hedgecost'] = '<p class="term">hedge cost</p> <p class="definition">An expense for trading and holding derivative securities.  <span class="return">See Module 2, Chapter 6.</span></p>';
terms['ch9-gt-mortalityandexpenseriskcharge'] = '<p class="term">mortality and expense (M&amp;E) risk charge</p> <p class="definition">A periodic charge assessed on a variable (unit-linked) life insurance policy or annuity contract that compensates the insurer for the mortality and expense risks inherent in the assumptions made when designing the product.  <a href="../chapter9/page6.xhtml#loma371-ch9-span-88" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch11-gt-guaranteedlifetimewithdrawalbenefit'] = '<p class="term">guaranteed lifetime withdrawal benefit (GLWB)</p> <p class="definition">An annuity living benefit rider that guarantees minimum withdrawals to a variable annuity owner for life without being required to annuitize the contract. <span class="return">See Module 4, Chapter 11.</span></p>';
terms['ch4-gt-independentvariable'] = '<p class="term">independent variable</p> <p class="definition">A variable that influences the behavior of another variable. By convention, independent variables are labeled x.  <span class="return">See Module 1, Chapter 4.</span></p>';
terms['ch2-gt-investmentrisk'] = '<p class="term">investment risk</p> <p class="definition">The possibility that an investment may fail to generate some or all of an expected profit or that the insurer will lose all or part of the original investment.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch1-gt-technicalproductdesign'] = '<p class="term">technical product design</p> <p class="definition">The phase in the product development process in which a product&#8217;s financial structure is created. Also known as product design.  <span class="return">See Module 1, Chapter 1.</span></p>';
terms['ch5-gt-productdevelopmentteam'] = '<p class="term">product development team</p> <p class="definition">A type of cross-functional team that usually performs hands-on development of a new product.  <span class="return">See Module 2, Chapter 5.</span></p>';
terms['ch11-gt-singlelifewithrefundannuity'] = '<p class="term">single life with refund annuity</p> <p class="definition">A life annuity payout option that guarantees specified periodic payments throughout the lifetime of a named individual&#8212;the annuitant&#8212;and also guarantees that a refund will be made if the annuitant dies before the total of the periodic payments made equals the amount paid for the annuity.  <span class="return">See Module 4, Chapter 11.</span></p>';
terms['ch1-gt-financialmodel'] = '<p class="term">financial model</p> <p class="definition">A computer-based mathematical model that approximates the operation of real-world financial processes.  <span class="return">See Module 1, Chapter 1.</span></p>';
terms['ch9-gt-indirectexpenses'] = '<p class="term">indirect expense</p> <p class="definition">An expense that cannot be traced to or that is not incurred for one specific product. Also called common cost.  <a href="../chapter9/page3.xhtml#loma371-ch9-span-11" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch4-gt-sensitivityanalysis'] = '<p class="term">sensitivity analysis</p> <p class="definition">A method of measuring the responsiveness of the outputs produced by a financial model to changes in the values of the model&#8217;s input variables. <span class="return">See Module 1, Chapter 4.</span></p>';
terms['ch3-gt-keyriskindicator'] = '<p class="term">key risk indicator (KRI)</p> <p class="definition">A quantitative metric that indicates the level of operational risk exposures at a given point of time in order to help companies forecast future occurrences.  <span class="return">See Module 1, Chapter 3.</span></p>';
terms['ch1-gt-liquidity'] = '<p class="term">liquidity</p> <p class="definition">The condition of having enough cash&#8212;or assets that can be readily converted into cash for an approximation of their underlying value&#8212;available to meet obligations as they come due. <span class="return">See Module 1, Chapter 1.</span></p>';
terms['ch2-gt-standardvaluationlaw'] = '<p class="term">Standard Valuation Law (SVL)</p> <p class="definition">An NAIC model law that (1) imposes minimum requirements on the size of insurance and annuity reserves and (2) establishes guidelines insurers must follow when calculating their reserves. <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch4-gt-deterministicmodeling'] = '<p class="term">deterministic modeling</p> <p class="definition">A form of quantitative modeling that simulates the real-world interactions of a stated set of input variables and produces a single set of output variables.  <span class="return">See Module 1, Chapter 4.</span></p>';
terms['ch2-gt-businessprocessrisk'] = '<p class="term">business process risk</p> <p class="definition">The risk of a loss related to the processes used by an organization to create, sell, and service a product.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch7-gt-initialinvestment'] = '<p class="term">initial investment</p> <p class="definition">For a new product, the amount of capital that an insurer must invest to establish the product. <a href="../chapter7/page5.xhtml#loma371-ch7-span-79" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch9-gt-costaccumulation'] = '<p class="term">cost accumulation</p> <p class="definition">The process of capturing all company costs and classifying them in meaningful ways.  <a href="../chapter9/page3.xhtml#loma371-ch9-span-15" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch5-gt-productrevisionproject'] = '<p class="term">product revision project</p> <p class="definition">A product development project that involves broader changes than does a rate change project but fewer changes than does a new product project.  <span class="return">See Module 2, Chapter 5.</span></p>';
terms['ch2-gt-underwritingrisk'] = '<p class="term">underwriting risk</p> <p class="definition">The specific risk that insurers assume through the insurance and annuity contracts they underwrite. <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch2-gt-solvencylaws'] = '<p class="term">solvency laws</p> <p class="definition">Laws designed to ensure that insurance companies are financially able to meet their debts and to pay policy benefits when they come due.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch8-gt-mortalitytablewithprojection'] = '<p class="term">mortality table with projection</p> <p class="definition">A type of mortality table in which the rates have been adjusted using the projection method to show future changes in mortality. <a href="../chapter8/page8.xhtml#ch8-span-ch8-gt-mortalitytablewithprojection" class="return">Return to text.</a></p>';
terms['ch1-gt-risktolerance'] = '<p class="term">risk tolerance</p> <p class="definition">An organization&#8217;s willingness and readiness to bear a risk to achieve its objectives.  <span class="return">See Module 1, Chapter 1.</span></p>';
terms['ch4-gt-fixedassumptions'] = '<p class="term">fixed assumptions</p> <p class="definition">In financial modeling, variables for which actuaries assign a specific value or proportional relationship, and the value or proportion remains unchanged throughout multiple iterations of a model. <span class="return">See Module 1, Chapter 4.</span></p>';
terms['ch3-gt-internalcontrol'] = '<p class="term">internal control</p> <p class="definition">The process a company takes to encourage adherence to its policies, promote operating efficiency, and safeguard the organization&#8217;s assets. <span class="return">See Module 1, Chapter 3.</span></p>';
terms['ch8-gt-selectmortalityperiod'] = '<p class="term">select mortality period</p> <p class="definition">A specified period of time following an underwriting evaluation of a given individual. <a href="../chapter8/page5.xhtml#loma371-ch8-span-34" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch9-gt-marginalcosting'] = '<p class="term">marginal costing</p> <p class="definition">An accounting method for estimating product-related expenses in which only direct expenses are counted. Also known as direct costing.  <a href="../chapter9/page4.xhtml#loma371-ch9-span-30" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch2-gt-principlesbasedreserving'] = '<p class="term">principles-based reserving (PBR)</p> <p class="definition">An approach to reserve valuation in which actuarial assumptions, and therefore reserve levels, change as the economic environment and other risk factors change.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch11-gt-yieldspreadprovision'] = '<p class="term">yield spread provision</p> <p class="definition">For fixed indexed annuities, a provision which states that the insurer always deducts a specified percentage from the growth in the reference index.  <span class="return">See Module 4, Chapter 11.</span></p>';
terms['ch2-gt-liquidation'] = '<p class="term">liquidation</p> <p class="definition">In an insurance receivership, a process by which a receiver either transfers all of the insurer&#8217;s business&#8212;including its reserve liabilities and assets&#8212;to other insurers or sells the insurer&#8217;s assets and terminates the insurer&#8217;s business.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch4-gt-rangeestimate'] = '<p class="term">range estimate</p> <p class="definition">In financial modeling, an estimate that provides a range of possible outcome values.  <span class="return">See Module 1, Chapter 4.</span></p>';
terms['ch11-gt-jointlifeannuity'] = '<p class="term">joint life annuity</p> <p class="definition">A life annuity payout option that typically covers more than one life and guarantees periodic payments until one of the covered individuals dies, and then pays nothing more.  <span class="return">See Module 4, Chapter 11.</span></p>';
terms['ch1-gt-stockholderdividends'] = '<p class="term">stockholder dividends</p> <p class="definition">A portion of a stock insurer&#8217;s capital paid&#8212;in cash or in additional shares of stock&#8212;to the owners of the insurer&#8217;s stock.  <span class="return">See Module 1, Chapter 1.</span></p>';
terms['ch3-gt-riskmap'] = '<p class="term">risk map</p> <p class="definition">A map that categorizes each risk identified for a particular operational or business process along two dimensions&#8212;(1) impact or significance and (2) likelihood. Also known as risk heat map. <span class="return">See Module 1, Chapter 3.</span></p>';
terms['ch11-gt-payoutperiod'] = '<p class="term">payout period</p> <p class="definition">For an annuity contract, the period during which the insurer makes periodic income payments to the payee. Also known as the liquidation period. <span class="return">See Module 4, Chapter 11.</span></p>';
terms['ch10-gt-singlepremium'] = '<p class="term">single premium</p> <p class="definition">For life insurance products, a form of premium payment in which one lump sum covers all of the financial considerations for the life of the contract; typically, one-year term life insurance policies are purchased with a single premium.  <span class="return">See Module 4, Chapter 10.</span></p>';
terms['ch2-gt-marketrisk'] = '<p class="term">market risk</p> <p class="definition">The risk of losses or reduced investment returns caused by movements in overall market conditions or in a specific sector of the market.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch11-gt-indexcredits'] = '<p class="term">index credits</p> <p class="definition">For fixed indexed annuities, the monetary credits that are awarded to the contract&#8217;s value.  <span class="return">See Module 4, Chapter 11.</span></p>';
terms['ch2-gt-policyholderbehaviorrisk'] = '<p class="term">policyholder behavior risk</p> <p class="definition">The risk that policyholder actions or decisions will adversely affect product performance. Also known as customer behavior risk.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch10-gt-longtermcareinsurancebenefit'] = '<p class="term">long-term care (LTC) insurance benefit</p> <p class="definition">An accelerated death benefit under which the insurer agrees to pay monthly benefits to a life insurance policyowner if the insured requires constant care&#8212;in his own home or a qualified facility&#8212;for a specified medical condition.  <span class="return">See Module 4, Chapter 10.</span></p>';
terms['ch2-gt-competitionrisk'] = '<p class="term">competition risk</p> <p class="definition">The risk that an insurer&#8217;s relative market position will be adversely affected by the actions of its direct competitors, changes in industry structure, or changes in standards for the use of technology. <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch2-gt-businessrisk'] = '<p class="term">business risk</p> <p class="definition">The risk that changes in a company&#8217;s external environment will affect its operations.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch11-gt-variableannuity'] = '<p class="term">variable annuity</p> <p class="definition">An annuity contract under which the amount of the accumulated value and the amount of the annuity payments fluctuate in accordance with the performance of the selected investment funds. The contract owner bears most or all of the risk of losses from unprofitable investments.  <span class="return">See Module 4, Chapter 11.</span></p>';
terms['ch5-gt-policyfiling'] = '<p class="term">policy filing</p> <p class="definition">The act of submitting a policy contract form and any other legally required forms and documents to the appropriate state insurance department for approval.  <span class="return">See Module 2, Chapter 5.</span></p>';
terms['ch2-gt-cyberrisk'] = '<p class="term">cyber risk</p> <p class="definition">The risk of a financial loss, business disruption, theft of proprietary information, or damage to the reputation of an organization caused by failure in a process, procedure, or technology involving interconnected systems.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch2-gt-receivership'] = '<p class="term">receivership</p> <p class="definition">An arrangement in which an individual&#8212;known as a receiver or conservator&#8212;is appointed by a court to hold and administer an insolvent insurer&#8217;s assets and liabilities. Also known as conservatorship.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch9-gt-firstyearcommissions'] = '<p class="term">first-year commission</p> <p class="definition">A commission on life insurance policies that is payable when a contract is purchased and is equal to a stated percentage of the premium the insurer receives during the first policy year. <a href="../chapter9/page4.xhtml#loma371-ch9-span-36" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch3-gt-durationgapreport'] = '<p class="term">duration gap report</p> <p class="definition">An asset-liability management (ALM) report that provides a snapshot of the insurer&#8217;s asset-liability match at the time of the report.  <span class="return">See Module 1, Chapter 3.</span></p>';
terms['ch7-gt-spreadcompression'] = '<p class="term">spread compression</p> <p class="definition">The narrowing of an insurer&#8217;s interest spread. <a href="../chapter7/page4.xhtml#loma371-ch7-span-55" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch2-gt-administrativesupervision'] = '<p class="term">administrative supervision</p> <p class="definition">A legal condition under which an insurer may be required to obtain the insurance commissioner&#8217;s permission before taking any of a variety of specified actions.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch7-gt-separateaccounts'] = '<p class="term">separate account</p> <p class="definition">For an insurer, a portfolio of assets that supports such products as variable life insurance and variable annuities. Also known as separate account portfolio or segregated funds portfolio. <a href="../chapter7/page3.xhtml#loma371-ch7-span-7" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch2-gt-mortalityrisk'] = '<p class="term">mortality risk</p> <p class="definition">The risk that actual mortality&#8212;the incidence of death in a specified group of people&#8212;will differ from expectations.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch6-gt-costofbenefits'] = '<p class="term">cost of benefits</p> <p class="definition">For an insurance or annuity product, the value of the contractually required benefits that the product promises to pay; the projected cost of benefits generally equals the sum of each potential benefit payment to a customer multiplied by the expected probability that the benefit will be payable. <span class="return">See Module 2, Chapter 6.</span></p>';
terms['ch11-gt-fixedamountoption'] = '<p class="term">fixed amount option</p> <p class="definition">A guaranteed annuity payout option in which the contract owner chooses the amount of income that the insurer will pay periodically; the insurer pays the same amount on a regular basis until the contract value is exhausted. <span class="return">See Module 4, Chapter 11.</span></p>';
terms['ch5-gt-marketanalysis'] = '<p class="term">market analysis</p> <p class="definition">An evaluation of all environmental factors that might affect sales of a proposed product.  <span class="return">See Module 2, Chapter 5.</span></p>';
terms['ch7-gt-accumulationunits'] = '<p class="term">accumulation unit</p> <p class="definition">An ownership share in a selected subaccount of a separate account held during the accumulation period.  <a href="../chapter7/page4.xhtml#loma371-ch7-span-54" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch6-gt-indeterminatepremiumstructure'] = '<p class="term">indeterminate premium structure</p> <p class="definition">A variation in premium rate structures for individual life insurance products that allows the company to periodically reset premium rates on products while guaranteeing customers that premiums will not exceed a maximum specified level.  <span class="return">See Module 2, Chapter 6.</span></p>';
terms['ch11-gt-annuitant'] = '<p class="term">annuitant</p> <p class="definition">The person whose lifetime the insurance company uses to determine the amount and duration of periodic income payments payable under an annuity contract. <span class="return">See Module 4, Chapter 11.</span></p>';
terms['ch8-gt-lifeinsurancemortalitytable'] = '<p class="term">life insurance mortality table</p> <p class="definition">A type of mortality table that shows the projected mortality rates and survival rates for a population of life insureds only.  <a href="../chapter8/page5.xhtml#loma371-ch8-span-29" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch2-gt-ratingagencycapital'] = '<p class="term">rating agency capital</p> <p class="definition">The minimum standard of capital that an insurer must maintain in order to receive a favorable quality rating from a specific rating agency.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch5-gt-productportfolio'] = '<p class="term">product portfolio</p> <p class="definition">In the context of product development, the array of all products a company offers for sale, categorized by line of business.  <span class="return">See Module 2, Chapter 5.</span></p>';
terms['ch4-gt-scenariotesting'] = '<p class="term">scenario testing</p> <p class="definition">In financial modeling, a method of evaluating modeling results that involves entering different sets of data into a model and then determining how changes in the input data affect the model&#8217;s output data.  <span class="return">See Module 1, Chapter 4.</span></p>';
terms['ch7-gt-costofcapital'] = '<p class="term">cost of capital</p> <p class="definition">The overall percentage cost that an insurer pays for the funds it employs.  <a href="../chapter7/page5.xhtml#loma371-ch7-span-85" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch10-gt-levelannualpremium'] = '<p class="term">level annual premium (LAP)</p> <p class="definition">For life insurance products, any set of equal annual payments having a present value equal to a given single premium.  <span class="return">See Module 4, Chapter 10.</span></p>';
terms['ch4-gt-trendanalysis'] = '<p class="term">trend analysis</p> <p class="definition">A form of technical analysis that projects the future movement of specified variables based on historical patterns. <span class="return">See Module 1, Chapter 4.</span></p>';
terms['ch5-gt-newproductrisk'] = '<p class="term">new product risk</p> <p class="definition">Risk that arises from developing and supporting new life insurance and annuity products. <span class="return">See Module 2, Chapter 5.</span></p>';
terms['ch9-gt-nonguaranteedelements'] = '<p class="term">nonguaranteed element (NTE)</p> <p class="definition">A product feature that allows an insurer to reward customers when the insurer has experienced or anticipates it will experience favorable deviations from its assumptions, or, conversely, allows an insurer to charge customers more when the insurer has experienced or anticipates it will experience unfavorable deviations from its assumptions. <a href="../chapter9/page5.xhtml#loma371-ch9-span-66" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch3-gt-diversification'] = '<p class="term">diversification</p> <p class="definition">A risk management strategy for spreading risk by investing in different assets having different risk profiles.  <span class="return">See Module 1, Chapter 3.</span></p>';
terms['ch11-gt-fixedindexedannuity'] = '<p class="term">fixed indexed annuity (FIA)</p> <p class="definition">A type of annuity that offers the contract owner specified principal and interest rate guarantees, as well as the possibility of additional earnings based on changes in an index by linking crediting on the accumulation value to a published index of stock market prices. Also known as an indexed annuity.  <span class="return">See Module 4, Chapter 11.</span></p>';
terms['ch9-gt-contractmaintenancecharge'] = '<p class="term">contract maintenance charge</p> <p class="definition">For variable (unit-linked) life insurance and annuity products, a periodic charge assessed to cover general maintenance costs, such as preparation of account and other statements.  <a href="../chapter9/page6.xhtml#loma371-ch9-span-87" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch2-gt-ownriskandsolvencyassessment'] = '<p class="term">Own Risk and Solvency Assessment (ORSA)</p> <p class="definition">A solvency program that requires insurers to regularly analyze all material risks that could affect the insurer&#8217;s ability to meet its financial obligations to its policyowners.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch4-gt-stochasticassumptions'] = '<p class="term">stochastic assumptions</p> <p class="definition">In financial modeling, variables that are selected randomly from a specified statistical distribution applied across multiple simulations or scenarios.  <span class="return">See Module 1, Chapter 4.</span></p>';
terms['ch4-gt-extrapolation'] = '<p class="term">extrapolation</p> <p class="definition">In financial modeling, the process of estimating values outside of a known range on the basis of other values derived from direct observation. <span class="return">See Module 1, Chapter 4.</span></p>';
terms['ch8-gt-mortalityexperience'] = '<p class="term">mortality experience</p> <p class="definition">The number of deaths that actually occur in a given group of people.  <a href="../chapter8/page3.xhtml#loma371-ch8-span-7" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch4-gt-dynamicfinancialanalysis'] = '<p class="term">dynamic financial analysis (DFA)</p> <p class="definition">The use of simulation modeling and multiple scenario testing to project an insurer&#8217;s assets, liabilities, and owners&#8217; equity as of a given valuation date and to compare the values of those variables at various times after the valuation date.  <span class="return">See Module 1, Chapter 4.</span></p>';
terms['ch10-gt-netamountatrisk'] = '<p class="term">net amount at risk</p> <p class="definition">For a universal life (UL) insurance policy, the amount of the insurer&#8217;s funds that would be required at any given time to pay the policy death benefit; this amount is generally equal to the difference between the UL policy&#8217;s death benefit and its cash value.  <span class="return">See Module 4, Chapter 10.</span></p>';
terms['ch10-gt-variableuniverallifeinsurance'] = '<p class="term">variable univeral life (VUL) insurance</p> <p class="definition">A type of universal life insurance that combines the premium and death benefit flexibility of universal life insurance with the investment flexibility and risk of variable life insurance. <span class="return">See Module 4, Chapter 10.</span></p>';
terms['ch2-gt-downgraderisk'] = '<p class="term">downgrade risk</p> <p class="definition">In the context of investing, the type of credit risk that involves the risk of deterioration in a credit rating assigned to an issuer of a debt security.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch2-gt-distributionrisk'] = '<p class="term">distribution risk</p> <p class="definition">The risk of financial losses resulting from activities and resources involved in making products available to customers. <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch7-gt-discountingperiod'] = '<p class="term">discounting period</p> <p class="definition">Each of the interest periods used in present value calculations.  <a href="../chapter7/page3.xhtml#loma371-ch7-span-15" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch2-gt-modelhazardousconditionregulation'] = '<p class="term">Model Hazardous Condition Regulation</p> <p class="definition">A model law developed by the National Association of Insurance Commissioners (NAIC) that describes the mandatory corrective actions an insurance commissioner can order an insurer to take to improve its financial condition.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch2-gt-actuarialopinionandmemorandum'] = '<p class="term">Actuarial Opinion and Memorandum (AOM)</p> <p class="definition">A formal asset-liability management (ALM) report that consists of two documents: an actuarial opinion and an actuarial memorandum. <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch1-gt-actuarialcontrolcycle'] = '<p class="term">actuarial control cycle</p> <p class="definition">A process of actuarial practices expressed as a process of three repeated steps: define the need, design the solution, and monitor the results.  <span class="return">See Module 1, Chapter 1.</span></p>';
terms['ch9-gt-vestedcommission'] = '<p class="term">vested commission</p> <p class="definition">A commission that is guaranteed payable to an agent whether or not the agent represents the company when the commission becomes due. <a href="../chapter9/page4.xhtml#loma371-ch9-span-38" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch5-gt-ideascreening'] = '<p class="term">idea screening</p> <p class="definition">An activity in which a team quickly and inexpensively evaluates new product ideas to separate workable ideas from those that have little or no potential.  <span class="return">See Module 2, Chapter 5.</span></p>';
terms['ch3-gt-investmentactivityreport'] = '<p class="term">investment activity report</p> <p class="definition">An asset-liability management (ALM) report that specifies the details of all investment portfolio transactions, including all asset acquisitions and all dispositions of assets from the investment portfolio through sales, prepayments (redemptions), or repayment at maturity. <span class="return">See Module 1, Chapter 3.</span></p>';
terms['ch7-gt-pricedepreciation'] = '<p class="term">price depreciation</p> <p class="definition">A decrease in the market value of an invested asset.  <a href="../chapter7/page3.xhtml#loma371-ch7-span-9" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch1-gt-lawoflargenumbers'] = '<p class="term">law of large numbers</p> <p class="definition">A mathematical concept which states that, under normal circumstances, the more times a particular event is observed, the more likely it is that the observations will approximate the "true" probability that the event will occur.  <span class="return">See Module 1, Chapter 1.</span></p>';
terms['ch11-gt-contractowner'] = '<p class="term">contract owner</p> <p class="definition">The person or other entity who owns and exercises all the rights and privileges of an annuity contract.  <span class="return">See Module 4, Chapter 11.</span></p>';
terms['ch11-gt-annuitization'] = '<p class="term">annuitization</p> <p class="definition">The process of converting the accumulated value of a deferred annuity into the annuity&#8217;s periodic income payments. <span class="return">See Module 4, Chapter 11.</span></p>';
terms['ch4-gt-variable'] = '<p class="term">variable</p> <p class="definition">An item of data whose numerical value varies.  <span class="return">See Module 1, Chapter 4.</span></p>';
terms['ch4-gt-stochasticmodeling'] = '<p class="term">stochastic modeling</p> <p class="definition">A form of modeling in which an automatic process randomly assigns values to specified input data in order to create a large number of scenarios, conduct numerous process iterations as needed, and produce output data that can be described in the form of a probability distribution. <span class="return">See Module 1, Chapter 4.</span></p>';
terms['ch11-gt-fixedfundoption'] = '<p class="term">fixed fund option</p> <p class="definition">For variable life insurance and annuity products, a fund option which guarantees a fixed rate of interest for a specified period of time; premiums allocated to a fixed fund option are invested with the general account.  <span class="return">See Module 4, Chapter 11.</span></p>';
terms['ch11-gt-ridercharge'] = '<p class="term">rider charge</p> <p class="definition">The additional charge to purchase a variable annuity guarantee rider. Also known as a rider premium.  <span class="return">See Module 4, Chapter 11.</span></p>';
terms['ch2-gt-liquidityrisk'] = '<p class="term">liquidity risk</p> <p class="definition">The risk that an asset might not be easily sold for its underlying value.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch10-gt-universallifeinsurance'] = '<p class="term">universal life (UL) insurance</p> <p class="definition">A form of cash value life insurance that is characterized by its flexible premiums, its flexible face amount, its flexible death benefit amount, and its separation of the three primary policy elements&#8212;mortality charges, interest rate, and expense charges.  <span class="return">See Module 4, Chapter 10.</span></p>';
terms['ch5-gt-omnichannelservicestrategy'] = '<p class="term">omnichannel service strategy</p> <p class="definition">A strategy to seamlessly provide a similar, high-quality customer experience across multiple, integrated communication platforms.  <span class="return">See Module 2, Chapter 5.</span></p>';
terms['ch7-gt-refinance'] = '<p class="term">refinance</p> <p class="definition">To make new borrowing arrangements, usually because of a drop in market interest rates.  <a href="../chapter7/page4.xhtml#loma371-ch7-span-59" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch11-gt-guaranteedannuitypayoutoption'] = '<p class="term">guaranteed annuity payout option</p> <p class="definition">A type of annuity payout option that provides regular payments of either a designated amount or for a designated period and are not linked to any life expectancy or mortality risk.  <span class="return">See Module 4, Chapter 11.</span></p>';
terms['ch8-gt-expectedmortality'] = '<p class="term">expected mortality</p> <p class="definition">The number or rate of deaths statistically likely to occur in a group of people at a given age. <a href="../chapter8/page3.xhtml#loma371-ch8-span-9" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch11-gt-singlelifeannuity'] = '<p class="term">single life annuity</p> <p class="definition">A life annuity payout option that provides periodic income payments in an amount based on both the single premium and the annuitant&#8217;s life expectancy. Under this option, the insurer provides no benefit payments after the annuitant&#8217;s death. Also known as a life only annuity and a straight life annuity. <span class="return">See Module 4, Chapter 11.</span></p>';
terms['ch1-gt-datascientist'] = '<p class="term">data scientist</p> <p class="definition">An individual who uses techniques from statistics and computer programming to (1) identify patterns and trends in data and (2) create mathematical models that forecast business outcomes. <span class="return">See Module 1, Chapter 1.</span></p>';
terms['ch6-gt-projectschedule'] = '<p class="term">project schedule</p> <p class="definition">A project planning timetable that shows the times required for the completion of planned tasks and the relationships among these tasks. <span class="return">See Module 2, Chapter 6.</span></p>';
terms['ch8-gt-mortalitycredit'] = '<p class="term">mortality credit</p> <p class="definition">A monetary value that an insurer (1) allotted to payments for an annuity, and (2) did not pay in full due to the death of the annuitant. <a href="../chapter8/page7.xhtml#loma371-ch8-span-63" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch7-gt-interestrateassumption'] = '<p class="term">interest-rate assumption</p> <p class="definition">For an insurance product, the assumed rate of investment earnings on the product in a specified period after investment expenses have been covered. Also known as assumed interest rate. <a href="../chapter7/page4.xhtml#loma371-ch7-span-51" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch8-gt-selectandultimatemortalitytable'] = '<p class="term">select and ultimate mortality table</p> <p class="definition">A type of mortality table that combines coordinated sets of select mortality rates and ultimate mortality rates. Also known as an aggregate mortality table. <a href="../chapter8/page5.xhtml#loma371-ch8-span-37" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch8-gt-valuationmortalitytables'] = '<p class="term">valuation mortality table</p> <p class="definition">A type of mortality table that has a margin built into the mortality rates, is used to calculate policy reserves, and is inherently more conservative than a basic mortality table.  <a href="../chapter8/page5.xhtml#loma371-ch8-span-32" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch8-gt-unisexmortalitytables'] = '<p class="term">unisex mortality table</p> <p class="definition">A type of mortality table that shows a single set of mortality rates to be used for both males and females.  <a href="../chapter8/page5.xhtml#loma371-ch8-span-39" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch2-gt-regulatorycapital'] = '<p class="term">regulatory capital</p> <p class="definition">The legal minimum standard of capital that an insurer must maintain in order to be considered solvent by the regulatory authorities.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch11-gt-returnofpremiumprovision'] = '<p class="term">return of premium provision</p> <p class="definition">For fixed indexed annuities, a provision which specifies that the insurer promises to pay the contract owner the full value of premiums&#8212;minus any partial withdrawals&#8212;upon surrender if the contract has not built an accumulated value greater than the value promised in the provision. <span class="return">See Module 4, Chapter 11.</span></p>';
terms['ch7-gt-futurevalue'] = '<p class="term">future value (FV)</p> <p class="definition">The amount that an original sum is expected to be worth at a specified future date, given a specified interest rate.  <a href="../chapter7/page3.xhtml#loma371-ch7-span-12" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch8-gt-proprietarymortalitytables'] = '<p class="term">proprietary mortality table</p> <p class="definition">A type of mortality table that a single insurance company develops for internal use, based largely on its experience with its own customers. <a href="../chapter8/page5.xhtml#loma371-ch8-span-40" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch9-gt-unitcost'] = '<p class="term">unit cost</p> <p class="definition">The amount of incurred expense attributable to a single measured amount of product. <a href="../chapter9/page4.xhtml#loma371-ch9-span-25" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch3-gt-hedging'] = '<p class="term">hedging</p> <p class="definition">A risk management strategy that involves holding an asset with characteristics that counterbalance one or more of the risks in the investor&#8217;s risk array.  <span class="return">See Module 1, Chapter 3.</span></p>';
terms['ch3-gt-durationmatching'] = '<p class="term">duration matching</p> <p class="definition">A risk management strategy that involves matching the duration statistics for fixed-income assets such as bonds with the duration statistics for the insurance company liabilities that the assets support. <span class="return">See Module 1, Chapter 3.</span></p>';
terms['ch2-gt-baselcommitteeonbankingsupervision'] = '<p class="term">Basel Committee on Banking Supervision (BCBS)</p> <p class="definition">A membership organization comprising representatives of bank supervisory authorities and central banks from countries with prominent economies whose mission is to provide a forum for discussing bank supervision matters worldwide.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch2-gt-internationalorganizationforstandardization'] = '<p class="term">International Organization for Standardization (ISO)</p> <p class="definition">An independent, non-governmental international standards-setting organization that consists of national standard bodies of 164 countries <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch7-gt-interestspread'] = '<p class="term">interest spread</p> <p class="definition">The difference between the rate of return an insurer earns on its investments and the current interest-crediting rate.  <a href="../chapter7/page4.xhtml#loma371-ch7-span-50" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch11-gt-lifeannuity'] = '<p class="term">life annuity</p> <p class="definition">An annuity option that guarantees the insurer will provide periodic income payments for only as long as the annuitant lives.  <span class="return">See Module 4, Chapter 11.</span></p>';
terms['ch11-gt-indexedcrediting'] = '<p class="term">indexed crediting</p> <p class="definition">For fixed indexed annuities, guarantees that the insurance company will periodically award monetary credits, known as index credits, to the contract&#8217;s accumulation value on a specified basis related to a specified external index of stock market price movements. <span class="return">See Module 4, Chapter 11.</span></p>';
terms['ch9-gt-inthemoney'] = '<p class="term">in the money (ITM)</p> <p class="definition">A situation in which the benefits available from ownership options or riders are more favorable for the customer than the benefits available under the contract itself.  <a href="../chapter9/page5.xhtml#loma371-ch9-span-63" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch6-gt-investmentpolicy'] = '<p class="term">investment policy</p> <p class="definition">A policy that actuaries and investment experts set to manage the investment risk associated with new products; specifies limits on the asset types and the proportions of the assets the company will use to support a product, as well as any strategies needed to balance a new product&#8217;s unmanaged liability risks.  <span class="return">See Module 2, Chapter 6.</span></p>';
terms['ch10-gt-nonforfeiturevalue'] = '<p class="term">nonforfeiture value</p> <p class="definition">The benefit that an insurer guarantees to a policyowner if the insurance contract lapses.  <span class="return">See Module 4, Chapter 10.</span></p>';
terms['ch8-gt-cohort'] = '<p class="term">cohort</p> <p class="definition">A defined group of people.  <a href="../chapter8/page2.xhtml#loma371-ch8-span-5" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch3-gt-duration'] = '<p class="term">duration</p> <p class="definition">A statistic that measures the price sensitivity of an asset to changes in interest rates; can be defined as the time-weighted value of expected cash inflows, expressed in years.  <span class="return">See Module 1, Chapter 3.</span></p>';
terms['ch3-gt-riskcommittee'] = '<p class="term">risk committee</p> <p class="definition">A senior management group whose primary role is to provide overall guidance and control of an insurer&#8217;s risk management efforts. <span class="return">See Module 1, Chapter 3.</span></p>';
terms['ch2-gt-humanresourcesrisk'] = '<p class="term">human resources risk</p> <p class="definition">The risk that a loss will occur as a result of a company&#8217;s failure to properly train or evaluate prospective and current employees.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch3-gt-interestcreditingstrategies'] = '<p class="term">interest-crediting strategies</p> <p class="definition">The formulas and criteria a company uses to set the interest rates it will credit for interest-sensitive products. <span class="return">See Module 1, Chapter 3.</span></p>';
terms['ch9-gt-standardcosts'] = '<p class="term">standard cost</p> <p class="definition">A cost estimate representing the average amount of a given type of expenditure for normal business operations.  <a href="../chapter9/page3.xhtml#loma371-ch9-span-16" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch6-gt-provisionforadversedeviation'] = '<p class="term">provision for adverse deviation (PAD)</p> <p class="definition">In product design, any amount included in the assumption values to provide an allowance for unexpected negative outcomes. <span class="return">See Module 2, Chapter 6.</span></p>';
terms['ch9-gt-frontendsalescharge'] = '<p class="term">front-end sales charge</p> <p class="definition">A one-time transaction charge imposed when an investor purchases shares in subaccount investment funds.  <a href="../chapter9/page6.xhtml#loma371-ch9-span-90" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch9-gt-costallocation'] = '<p class="term">cost allocation</p> <p class="definition">The accounting process of assigning an indirect cost to a particular cost object according to a formal procedure. Also known as expense allocation. <a href="../chapter9/page4.xhtml#loma371-ch9-span-31" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch11-gt-participationpercentageprovision'] = '<p class="term">participation percentage provision</p> <p class="definition">For fixed indexed annuities, the provision that stipulates the rate at which the excess of the reference index growth percentage is shared between the insurer and the contract owner. <span class="return">See Module 4, Chapter 11.</span></p>';
terms['ch10-gt-accidentaldeathbenefit'] = '<p class="term">accidental death benefit</p> <p class="definition">A supplemental life insurance policy benefit that requires the insurer to pay a specified amount of money in addition to the policy&#8217;s basic death benefit if an insured dies as a result of an accident. <span class="return">See Module 4, Chapter 10.</span></p>';
terms['ch9-gt-costallocationmethod'] = '<p class="term">cost allocation method</p> <p class="definition">The formal procedure used to assign indirect costs.  <a href="../chapter9/page4.xhtml#loma371-ch9-span-33" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch9-gt-variableexpense'] = '<p class="term">variable expense</p> <p class="definition">An expense amount that varies in direct proportion to some variation in a specified level of operating activity.  <a href="../chapter9/page3.xhtml#loma371-ch9-span-12" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch10-gt-criticalillnessbenefit'] = '<p class="term">critical illness benefit</p> <p class="definition">An accelerated death benefit under which the insurer agrees to pay a portion of the policy&#8217;s face amount to the policyowner if the insured suffers from one of a number of specified diseases. Also known as a dread disease (DD) benefit. <span class="return">See Module 4, Chapter 10.</span></p>';
terms['ch7-gt-hurdlerate'] = '<p class="term">hurdle rate</p> <p class="definition">The minimum percentage rate of return on capital that an insurer must achieve to cover its cost of capital. Also known as required return.  <a href="../chapter7/page5.xhtml#loma371-ch7-span-84" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch2-gt-generalmanagementrisk'] = '<p class="term">general management risk (C-4 risk)</p> <p class="definition">The contingency risk of losses resulting from the insurer&#8217;s ineffective general business practices or from the need to pay a special assessment to cover another insurer&#8217;s unsound business practices.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch3-gt-rightsizing'] = '<p class="term">rightsizing</p> <p class="definition">The elimination of nonessential employees or jobs within an organization. Also known as downsizing. <span class="return">See Module 1, Chapter 3.</span></p>';
terms['ch8-gt-assumedmortality'] = '<p class="term">assumed mortality</p> <p class="definition">The hypothetical or assumed number or rate of deaths in a given group of people.  <a href="../chapter8/page3.xhtml#loma371-ch8-span-10" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch2-gt-regulatoryrisk'] = '<p class="term">regulatory risk</p> <p class="definition">The risk created by changes in the regulatory environment. <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch2-gt-minimumcapitalstandard'] = '<p class="term">minimum capital standard</p> <p class="definition">The difference between a company&#8217;s total assets and total liabilities.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch7-gt-earnings'] = '<p class="term">earnings</p> <p class="definition">For an insurance or annuity product, the amount that a product adds to the insurance company&#8217;s capital in a given period; takes into account noncash adjustments, such as a product&#8217;s contribution to capital. <a href="../chapter7/page5.xhtml#loma371-ch7-span-81" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch2-gt-expenseloading'] = '<p class="term">expense loading</p> <p class="definition">The amount charged to cover each policy&#8217;s share of the expenses of operation. <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch2-gt-solvencyregulation'] = '<p class="term">solvency regulation</p> <p class="definition">Insurance regulation that addresses the need for insurance companies to be financially stable and capable of paying obligations and disbursing benefits when they are due. Also known as prudential regulation. <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch9-gt-administrativecharge'] = '<p class="term">administrative charge</p> <p class="definition">A charge an insurer levies to cover the cost of issuing certain types of life insurance or annuity contracts, making administrative changes to such contracts, preparing contract owner statements, and performing general maintenance activities. Also known as administrative expense charge, administrative fee, contract fee, or service fee. <a href="../chapter9/page6.xhtml#loma371-ch9-span-85" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch8-gt-selectmortalitytable'] = '<p class="term">select mortality table</p> <p class="definition">A type of mortality table that shows the expected mortality rates of people who have recently been underwritten for insurance policies.  <a href="../chapter8/page5.xhtml#loma371-ch8-span-33" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch2-gt-fraudrisk'] = '<p class="term">fraud risk</p> <p class="definition">The risk of a loss when one party intentionally deceives another party to induce that party to part with something of value, usually confidential information or money.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch5-gt-productmix'] = '<p class="term">product mix</p> <p class="definition">In the context of product development, a company&#8217;s total assortment of products within each line of business according to a monetary measure of business volume, such as premiums collected, reserves, or in the case of life insurance, face amount in force.  <span class="return">See Module 2, Chapter 5.</span></p>';
terms['ch6-gt-adversedeviation'] = '<p class="term">adverse deviation</p> <p class="definition">In product operations, a deviation that produces a decrease in actual product profitability compared to assumed product profitability.  <span class="return">See Module 2, Chapter 6.</span></p>';
terms['ch7-gt-internalrateofreturn'] = '<p class="term">internal rate of return (IRR)</p> <p class="definition">For a life insurance or an annuity product, the interest rate at which the product&#8217;s net cash flows must be discounted, using present value techniques, in order to exactly repay the insurer&#8217;s initial investment in the product.  <a href="../chapter7/page5.xhtml#loma371-ch7-span-83" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch11-gt-lifeannuitypayoutoption'] = '<p class="term">life annuity payout option</p> <p class="definition">A type of annuity payout option in which the insurer promises to make regular income payments for a period related to the life expectancy of one or more annuitants.  <span class="return">See Module 4, Chapter 11.</span></p>';
terms['ch2-gt-equityrisk'] = '<p class="term">equity risk</p> <p class="definition">The risk of a loss arising from movements in the direction of the stock, or equity, markets.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch7-gt-futurevalueinterestfactorstable'] = '<p class="term">future value interest factors (FVIF) table</p> <p class="definition">A table that lists future value interest factors.  <a href="../chapter7/page3.xhtml#loma371-ch7-span-23" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch5-gt-salesrevenue'] = '<p class="term">sales revenue</p> <p class="definition">The total dollar volume of sales, calculated by multiplying the sales volume by the average price per unit. <span class="return">See Module 2, Chapter 5.</span></p>';
terms['ch1-gt-solvency'] = '<p class="term">solvency</p> <p class="definition">An insurer&#8217;s ability to meet its financial obligations on time. <span class="return">See Module 1, Chapter 1.</span></p>';
terms['ch2-gt-modelsupervisionact'] = '<p class="term">Model Supervision Act</p> <p class="definition">A model law developed by the National Association of Insurance Commissioners (NAIC) that provides details about a state insurance department&#8217;s authority to require administrative supervision of an insurer. Also known as the Administrative Supervision Model Act.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch1-gt-risk'] = '<p class="term">risk</p> <p class="definition">The possibility of an unexpected outcome.  <span class="return">See Module 1, Chapter 1.</span></p>';
terms['ch4-gt-optimizationmodeling'] = '<p class="term">optimization modeling</p> <p class="definition">A form of modeling that focuses on finding an optimum solution to several simultaneous equations. <span class="return">See Module 1, Chapter 4.</span></p>';
terms['ch4-gt-cashflowtesting'] = '<p class="term">cash flow testing</p> <p class="definition">The use of simulation modeling to project the cash flows associated with an insurance company&#8217;s assets and liabilities as of a given valuation data and to compare the timing and amounts of asset and liability cash flows at various times after the valuation date. <span class="return">See Module 1, Chapter 4.</span></p>';
terms['ch11-gt-cap'] = '<p class="term">cap</p> <p class="definition">For fixed indexed annuities, an upper limit, expressed as a percent, on the rate of a reference index&#8217;s gain in value that the insurer credits to the annuity contract as interest. <span class="return">See Module 4, Chapter 11.</span></p>';
terms['ch2-gt-strategyrisk'] = '<p class="term">strategy risk</p> <p class="definition">The risk that a particular strategy selected by an insurer to grow sales, market share, profits, or distribution will not produce the desired results and instead lead to losses.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch8-gt-lifeexpectancy'] = '<p class="term">life expectancy</p> <p class="definition">The average number of years of life remaining for a group of people.  <a href="../chapter8/page5.xhtml#loma371-ch8-span-28" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch5-gt-ideageneration'] = '<p class="term">idea generation</p> <p class="definition">The process of searching for new product ideas that are consistent with the company&#8217;s overall product development strategy and the needs of its target markets.  <span class="return">See Module 2, Chapter 5.</span></p>';
terms['ch2-gt-expenserisk'] = '<p class="term">expense risk</p> <p class="definition">The risk that actual expenses will differ from the amounts assumed during product design.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch2-gt-creditrisk'] = '<p class="term">credit risk</p> <p class="definition">The risk that (1) a borrower defaults on debt obligations by failing to make payments of principal or interest on time, or (2) a lender sustains a financial loss based on an adverse change in a borrower&#8217;s creditworthiness.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch10-gt-pricingpremium'] = '<p class="term">pricing premium</p> <p class="definition">The monetary amount per unit of coverage that an insurer must collect from customers to cover any portion of the product&#8217;s cost of benefits or the company&#8217;s costs for supporting the product not covered by investment earnings.  <span class="return">See Module 4, Chapter 10.</span></p>';
terms['ch9-gt-renewalcommissions'] = '<p class="term">renewal commission</p> <p class="definition">A commission on life insurance policies that remain in force that is equal to a stated percentage of each premium paid for a specified number of years after the first policy year.  <a href="../chapter9/page4.xhtml#loma371-ch9-span-37" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch4-gt-experiencestudy'] = '<p class="term">experience study</p> <p class="definition">A compilation of data representing company or industry-wide historical experience with a specified modeling variable.  <span class="return">See Module 1, Chapter 4.</span></p>';
terms['ch5-gt-marketingplan'] = '<p class="term">marketing plan</p> <p class="definition">A document that defines a company&#8217;s product, price, distribution, and promotion strategies for reaching potential customers and meeting their needs. <span class="return">See Module 2, Chapter 5.</span></p>';
terms['ch11-gt-annualresetmethod'] = '<p class="term">annual reset method</p> <p class="definition">For fixed indexed annuities, an index-crediting mechanism that involves comparing the value of the reference index at the start of each contract year with the value at the end of the year. For the next year, the starting value is reset to the ending value from the previous year. Also known as ratchet method. <span class="return">See Module 4, Chapter 11.</span></p>';
terms['ch1-gt-productdevelopment'] = '<p class="term">product development</p> <p class="definition">The process companies use to create new products or modify existing products.  <span class="return">See Module 1, Chapter 1.</span></p>';
terms['ch2-gt-assetrisk'] = '<p class="term">asset risk (C-1 risk)</p> <p class="definition">The contingency risk that the insurer will lose money&#8212;other than from movements in market interest rates&#8212;on its investments in stocks, bonds, mortgages, and real estate.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch5-gt-competitiveintelligence'] = '<p class="term">competitive intelligence</p> <p class="definition">The systematic collection and analysis of publicly available information about competitors and ongoing developments in the marketing environment. <span class="return">See Module 2, Chapter 5.</span></p>';
terms['ch5-gt-productproposal'] = '<p class="term">product proposal</p> <p class="definition">A document used to present a product idea to an internal team that decides whether to allocate company resources toward developing the product idea.  <span class="return">See Module 2, Chapter 5.</span></p>';
terms['ch11-gt-jointandsurvivorannuity'] = '<p class="term">joint and survivor (J&amp;S) annuity</p> <p class="definition">A life annuity payout option that provides periodic income payments to two or more annuitants. The periodic income payments are based on the life expectancies of the annuitants and continue until both or all of the annuitants have died. Also known as joint and last survivor annuity.  <span class="return">See Module 4, Chapter 11.</span></p>';
terms['ch10-gt-flexiblepremium'] = '<p class="term">flexible premium</p> <p class="definition">For life insurance products, a form of premium payment in which a customer can make payments to the insurer at various times so as to increase the savings element in a contract that was established with a single initial premium; such premiums are used with universal life insurance.  <span class="return">See Module 4, Chapter 10.</span></p>';
terms['ch1-gt-profitability'] = '<p class="term">profitability</p> <p class="definition">A company&#8217;s overall degree of success in generating returns for its owners; the term refers both to a company&#8217;s ability to generate profit and its ability to increase the company&#8217;s wealth.  <span class="return">See Module 1, Chapter 1.</span></p>';
terms['ch2-gt-solvencyii'] = '<p class="term">Solvency II</p> <p class="definition">A European-sponsored solvency standard for insurance companies, designed to support solvency testing and solvency supervision in the public interest. Also known as the EU Directive on the Taking-up and Pursuit of the Business of Insurance and Reinsurance.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch4-gt-simulationmodeling'] = '<p class="term">simulation modeling</p> <p class="definition">The use of a real-world process model and extrapolation to emulate the behavior of a process over time. <span class="return">See Module 1, Chapter 4.</span></p>';
terms['ch9-gt-directexpenses'] = '<p class="term">direct expense</p> <p class="definition">A product expense incurred for or physically traceable to a specified life insurance or annuity product. Also known as traceable cost.  <a href="../chapter9/page3.xhtml#loma371-ch9-span-10" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch6-gt-productdevelopmentbudget'] = '<p class="term">product development budget</p> <p class="definition">A project planning document that shows the costs, such as staffing requirements, associated with each planned work activity and the anticipated revenue from the new product. <span class="return">See Module 2, Chapter 6.</span></p>';
terms['ch7-gt-generalaccount'] = '<p class="term">general account</p> <p class="definition">For an insurer, a portfolio of assets that supports the company&#8217;s contractual obligations to owners of the company&#8217;s guaranteed products, including whole life insurance, fixed-rate annuities, and other nonvariable products. Also known as general account portfolio. <a href="../chapter7/page3.xhtml#loma371-ch7-span-6" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch11-gt-singlelifeannuitywithperiodcertain'] = '<p class="term">single life annuity with period certain</p> <p class="definition">A life annuity payout option that guarantees periodic income payments throughout the lifetime of the annuitant and also guarantees that the payments will continue for at least a specified period. If the annuitant dies before the end of the specified period, the insurer provides the periodic income payments to the beneficiary until the end of the period. <span class="return">See Module 4, Chapter 11.</span></p>';
terms['ch4-gt-tailrisk'] = '<p class="term">tail risk</p> <p class="definition">The risk associated with values or scenarios in the tails at the end of a probability distribution. <span class="return">See Module 1, Chapter 4.</span></p>';
terms['ch2-gt-internationalassociationofinsurancesupervisors'] = '<p class="term">International Association of Insurance Supervisors (IAIS)</p> <p class="definition">An association that works globally to (1) improve regulatory supervision of the insurance industry, (2) maintain efficient, fair, safe, and stable insurance markets, (3) protect policyowners, (4) promote well-regulated insurance markets, and (5) support global financial stability.  <span class="return">See Module 1, Chapter 2.</span></p>';
terms['ch11-gt-guaranteedminimumwithdrawalbenefit'] = '<p class="term">guaranteed minimum withdrawal benefit (GMWB)</p> <p class="definition">An annuity living benefit rider that guarantees a minimum protected value against which a variable annuity contract owner may make annual withdrawals of a specified amount, without charge, even if the contract&#8217;s accumulated value falls below the protected value as a result of poor investment performance. <span class="return">See Module 4, Chapter 11.</span></p>';
terms['ch4-gt-interestsensitivecashflowtesting'] = '<p class="term">interest-sensitive cash flow testing</p> <p class="definition">A type of cash flow testing in which an insurer analyzes the effects of various interest-rate scenarios on cash flows.  <span class="return">See Module 1, Chapter 4.</span></p>';
terms['ch9-gt-backendsalescharge'] = '<p class="term">back-end sales charge</p> <p class="definition">A sales assessed when an investor sells a security.  <a href="../chapter9/page6.xhtml#loma371-ch9-span-91" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch9-gt-costing'] = '<p class="term">costing</p> <p class="definition">The process used during technical product design to set a product&#8217;s expense projections.  <a href="../chapter9/page4.xhtml#loma371-ch9-span-28" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch1-gt-riskreturntradeoff'] = '<p class="term">risk-return tradeoff</p> <p class="definition">The relationship between risk and return that can be summarized as follows: all other factors being equal, the greater the risk associated with an investment, the greater the potential return on the investment, and the lower the risk associated with an investment, the lower the potential return on the investment.  <span class="return">See Module 1, Chapter 1.</span></p>';
terms['ch8-gt-staticmortalitytables'] = '<p class="term">static mortality table</p> <p class="definition">A type of mortality table that has not been adjusted to account for future changes in mortality. <a href="../chapter8/page6.xhtml#loma371-ch8-span-50" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
terms['ch3-gt-outsourcing'] = '<p class="term">outsourcing</p> <p class="definition">The practice of hiring an external vendor to perform specified operations or functions.  <span class="return">See Module 1, Chapter 3.</span></p>';
terms['ch3-gt-investmentportfolioperformancereview'] = '<p class="term">investment portfolio performance review</p> <p class="definition">A quarterly asset-liability management (ALM) report that summarizes the company&#8217;s investment performance for the board of directors and the ALM committee.  <span class="return">See Module 1, Chapter 3.</span></p>';
terms['ch9-gt-depositbasedcommissionschedule'] = '<p class="term">deposit-based commission schedule</p> <p class="definition">For annuity sales, a commission schedule that pays commissions only on premium payments made by annuity owners.  <a href="../chapter9/page4.xhtml#loma371-ch9-span-42" class="return" epub:type="backlink" role="doc-backlink">Return to text.</a></p>';
